table_definition:
create table public.application_interview_slots (
  id uuid,
  created_at timestamp with time zone,
  selected_slots jsonb
);

create table public.application_demo_videos (
  id uuid,
  created_at timestamp with time zone,
  application_id uuid,
  demo_prompt jsonb
);

